import React, {useState} from 'react'

function TodoForm() {
const [input, setImput] = useState('')
  return (
    <form clasName='todo-form'>
        <input 
        type='text '
        placeholder= 'Add a todo'
        value={input}
        name='text'
        className='todo-input'
        />
        <button className="todo-button">Add todo</button>
    </form>
  )
}

export default TodoForm
